// Archivo: src/components/FindAuthorForm.jsx
import React, { useState } from 'react';

const FindAuthorForm = ({ userId, onNewInteraction }) => {
  const [phrase, setPhrase] = useState('');
  const [author, setAuthor] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setAuthor(null);
    try {
      const response = await fetch('/api/author', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ phrase, user_id: userId }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.error || 'Error al encontrar el autor');
      setAuthor(data.author);
      onNewInteraction();
    } catch (error) {
      console.error(error);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Encontrar Autor</h2>
      <textarea
        value={phrase}
        onChange={(e) => setPhrase(e.target.value)}
        required
      />
      <button type="submit" disabled={isLoading}>
        {isLoading ? 'Buscando...' : 'Encontrar Autor'}
      </button>
      {author && <div>Autor: {author}</div>}
    </form>
  );
};

export default FindAuthorForm;
