// Archivo: src/App.jsx
import React, { useState, useEffect } from 'react';
import { getAuth, signInAnonymously, onAuthStateChanged } from 'firebase/auth';
import { initializeApp } from "firebase/app";

// Importamos los componentes modulares
import SolveCryptogramForm from './components/SolveCryptogramForm';
import FindAuthorForm from './components/FindAuthorForm';
import GenerateCryptogramForm from './components/GenerateCryptogramForm';
import History from './components/History';

const App = () => {
  const [currentView, setCurrentView] = useState('solve');
  const [userId, setUserId] = useState(null);
  const [isAuthReady, setIsAuthReady] = useState(false);
  const [historyKey, setHistoryKey] = useState(0);

  const firebaseConfig = typeof __firebase_config !== 'undefined' ? JSON.parse(__firebase_config) : {};
  const app = initializeApp(firebaseConfig);
  const auth = getAuth(app);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      if (user) {
        setUserId(user.uid);
      } else {
        signInAnonymously(auth).then(cred => {
          setUserId(cred.user.uid);
        });
      }
      setIsAuthReady(true);
    });
    return () => unsubscribe();
  }, [auth]);

  const handleNewInteraction = () => {
    setHistoryKey(prevKey => prevKey + 1);
  };

  const renderContent = () => {
    switch (currentView) {
      case 'solve':
        return <SolveCryptogramForm userId={userId} onNewInteraction={handleNewInteraction} />;
      case 'author':
        return <FindAuthorForm userId={userId} onNewInteraction={handleNewInteraction} />;
      case 'generate':
        return <GenerateCryptogramForm userId={userId} onNewInteraction={handleNewInteraction} />;
      case 'history':
        return <History userId={userId} onNewInteraction={handleNewInteraction} key={historyKey} />;
      default:
        return null;
    }
  };

  const navItemClass = (view) => 
    `px-4 py-2 font-bold rounded-lg transition-colors duration-300 ${
      currentView === view
        ? 'bg-purple-600 text-white'
        : 'text-gray-700 hover:bg-gray-200'
    }`;

  if (!isAuthReady || !userId) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <p className="text-lg text-gray-500 animate-pulse">Cargando...</p>
      </div>
    );
  }

  return (
    <>
      <div id="alert-container"></div>
      <div className="min-h-screen bg-gray-100 p-8 font-sans antialiased">
        <style dangerouslySetInnerHTML={{ __html: `
          @import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;600;700&display=swap');
          body { font-family: 'Inter', sans-serif; }
        `}} />
        <script src="https://cdn.tailwindcss.com"></script>

        <div className="flex flex-col items-center">
          <header className="mb-8 text-center">
            <h1 className="text-5xl font-extrabold text-gray-900 mb-2">Cryptograms & Authors</h1>
            <p className="text-lg text-gray-600">Herramienta impulsada por IA para descifrar, encontrar y crear textos.</p>
            <p className="text-xs text-gray-400 mt-2">
              ID de Usuario: <span className="font-mono">{userId}</span>
            </p>
          </header>

          <nav className="flex space-x-2 bg-white p-2 rounded-xl shadow-md mb-8">
            <button onClick={() => setCurrentView('solve')} className={navItemClass('solve')}>
              Resolver
            </button>
            <button onClick={() => setCurrentView('author')} className={navItemClass('author')}>
              Autor
            </button>
            <button onClick={() => setCurrentView('generate')} className={navItemClass('generate')}>
              Generar
            </button>
            <button onClick={() => setCurrentView('history')} className={navItemClass('history')}>
              Historial
            </button>
          </nav>

          <main className="w-full">
            {renderContent()}
          </main>
        </div>
      </div>
    </>
  );
};

export default App;
